}

</style>

</head>

<body>

<h2>Data Processor</h2>

<div class="form-group">

<label for="column">Select Column:</label>

<select id="column">

<option value="A">Column A</option>

<option value="B">Column B</option>

<option value="C">Column C</option>

</select>

</div>

<button onclick="submitForm()">Process Data</button>

<div id="message" class="message"></div>

<script>

/**

* Submits form data to backend

*/

function submitForm() {

// Disable button and show loading

const button = document.querySelector('button');

const originalText = button.textContent;

button.disabled = true;

button.textContent = 'Processing...';

// Hide previous messages

hideMessage();

// Collect form data

const params = {

column: document.getElementById('column').value

};

// Validate

if (!params.column) {

showMessage('Please select a column', 'error');

button.disabled = false;

button.textContent = originalText;

return;

}

// Call backend

google.script.run

.withSuccessHandler(function(result) {

button.disabled = false;

button.textContent = originalText;

if (result.success) {

showMessage(result.message, 'success');

} else {

showMessage(result.message, 'error');

}

})

.withFailureHandler(function(error) {

button.disabled = false;

button.textContent = originalText;

showMessage('Error: ' + error.message, 'error');

})

.processData(params);

}

/**

* Displays message to user

* @param {string} text - Message text

* @param {string} type - Message type ('success' or 'error')

*/

function showMessage(text, type) {

const messageDiv = document.getElementById('message');

messageDiv.textContent = text;

messageDiv.className = 'message ' + type;

messageDiv.style.display = 'block';

}

/**

* Hides message element

*/

function hideMessage() {

const messageDiv = document.getElementById('message');

messageDiv.style.display = 'none';

messageDiv.className = 'message';

}

</script>

</body>

</html>

