Example 2: Advanced - Enterprise Integration

/**

* Advanced Project Management Integration System

* Integrates multiple Google services with external APIs

*/

const ADVANCED_CONFIG = {

SHEETS: {

PROJECTS: 'Projects',

TASKS: 'Tasks',

REPORTS: 'Reports'

},

API: {

EXTERNAL_URL: 'https://api.external-system.com',

RATE_LIMIT: 100,

TIMEOUT: 30000

},

NOTIFICATION: {

ADMIN_EMAIL: 'admin@company.com',

ALERT_THRESHOLDS: {

HIGH_PRIORITY: 1,

OVERDUE: 0

}

}

};

class ProjectManager {

constructor() {

this.spreadsheet = SpreadsheetApp.getActiveSpreadsheet();

this.cache = CacheService.getScriptCache();

this.errorLog = [];

}

async syncWithExternalSystem() {

const profiler = new PerformanceProfiler();

try {

profiler.start('full_sync');

// Fetch data from external API with retry logic

const externalData = await this.fetchWithRetry(

`${ADVANCED_CONFIG.API.EXTERNAL_URL}/projects`,

3 // max retries

);

// Process and validate data

const validatedData = this.validateAndTransform(externalData);

// Update sheets in batch operations

await this.updateProjectSheets(validatedData);

// Generate analytics and reports

const analytics = this.generateAnalytics(validatedData);

// Send notifications for critical items

await this.processNotifications(analytics);

profiler.end('full_sync');

this.logSuccess(profiler.getReport());

} catch (error) {

this.handleCriticalError(error);

throw error;

}

}

async fetchWithRetry(url, maxRetries) {

for (let attempt = 1; attempt <= maxRetries; attempt++) {

try {

const response = await UrlFetchApp.fetch(url, {

method: 'GET',

headers: {

'Authorization': `Bearer ${this.getAPIToken()}`,

'Content-Type': 'application/json'

},

muteHttpExceptions: true

});

if (response.getResponseCode() === 200) {

return JSON.parse(response.getContentText());

} else {

throw new Error(`API returned ${response.getResponseCode()}`);

}

} catch (error) {

if (attempt === maxRetries) throw error;

// Exponential backoff

const delay = Math.pow(2, attempt) * 1000;

Utilities.sleep(delay);

}

}

}

validateAndTransform(data) {

return data.map(project => {

// Comprehensive validation and transformation logic

return {

id: project.id || 'N/A',

name: project.name || 'Unnamed Project',

status: this.normalizeStatus(project.status),

dueDate: new Date(project.due_date),

priority: project.priority || 'Medium',

assignee: project.assignee || 'Unassigned'

};

});

}

async updateProjectSheets(data) {

const projectsSheet = this.spreadsheet.getSheetByName(ADVANCED_CONFIG.SHEETS.PROJECTS);

// Clear existing data (keep headers)

const lastRow = projectsSheet.getLastRow();

if (lastRow > 1) {

projectsSheet.getRange(2, 1, lastRow - 1, projectsSheet.getLastColumn()).clearContent();

}

// Batch insert new data

if (data.length > 0) {

const values = data.map(project => [

project.id,

project.name,

project.status,

project.dueDate,

project.priority,

project.assignee

]);

projectsSheet.getRange(2, 1, values.length, values[0].length).setValues(values);

}

}

generateAnalytics(data) {

return {

totalProjects: data.length,

overdue: data.filter(p => p.dueDate < new Date() && p.status !== 'Complete').length,

highPriority: data.filter(p => p.priority === 'High').length,

byStatus: this.groupBy(data, 'status'),

byAssignee: this.groupBy(data, 'assignee')

};

}

async processNotifications(analytics) {

const notifications = [];

if (analytics.overdue > ADVANCED_CONFIG.NOTIFICATION.ALERT_THRESHOLDS.OVERDUE) {

notifications.push({

type: 'overdue',

count: analytics.overdue,

priority: 'high'

});

}

if (analytics.highPriority > ADVANCED_CONFIG.NOTIFICATION.ALERT_THRESHOLDS.HIGH_PRIORITY) {

notifications.push({

type: 'high_priority',

count: analytics.highPriority,

priority: 'medium'

});

}

for (const notification of notifications) {

await this.sendAlert(notification);

}

}

async sendAlert(notification) {

const subject = this.getAlertSubject(notification);

const body = this.getAlertBody(notification);

MailApp.sendEmail({

to: ADVANCED_CONFIG.NOTIFICATION.ADMIN_EMAIL,

subject: subject,

htmlBody: body

});

}

handleCriticalError(error) {

this.errorLog.push({

timestamp: new Date(),

error: error.toString(),

stack: error.stack

});

// Send immediate error notification

MailApp.sendEmail({

to: ADVANCED_CONFIG.NOTIFICATION.ADMIN_EMAIL,

subject: 'üö® Critical Error in Project Management System',

body: `Error: ${error.toString()}\n\nStack: ${error.stack}`

});

}

// Utility methods

groupBy(array, key) {

return array.reduce((groups, item) => {

const group = item[key];

groups[group] = groups[group] || [];

groups[group].push(item);

return groups;

}, {});

}

getAPIToken() {

return PropertiesService.getScriptProperties().getProperty('API_TOKEN');

}

normalizeStatus(status) {

const statusMap = {

'in_progress': 'In Progress',

'completed': 'Complete',

'pending': 'Pending'

};

return statusMap[status] || status;

}

}

// Performance monitoring utility

class PerformanceProfiler {

constructor() {

this.startTime = null;

this.operations = new Map();

}

start(operation) {

this.startTime = Date.now();

this.operations.set(operation, { start: this.startTime });

}

end(operation) {

const endTime = Date.now();

const op = this.operations.get(operation);

if (op) {

op.end = endTime;

op.duration = endTime - op.start;

}

}

getReport() {

const report = [];

this.operations.forEach((data, operation) => {

report.push(`${operation}: ${data.duration}ms`);

});

return report.join(', ');

}

}

